(function(){window.MarketTickerUI=flight.component(function(){return this.attributes({askSelector:".ask .price",bidSelector:".bid .price",lastSelector:".last .price",priceSelector:".price"}),this.updatePrice=function(selector,price,trend){return selector.removeClass("text-up").removeClass("text-down").addClass(formatter.trend(trend)),selector.html(formatter.fixBid(price))},this.refresh=function(event,ticker){return this.updatePrice(this.select("askSelector"),ticker.sell,ticker.sell_trend),this.updatePrice(this.select("bidSelector"),ticker.buy,ticker.buy_trend),this.updatePrice(this.select("lastSelector"),ticker.last,ticker.last_trend)},this.after("initialize",function(){return this.on(document,"market::ticker",this.refresh)})})}).call(this);